INSERT INTO `sanpham` (`ID`, `Ten`, `Gia`, `HinhAnh`, `Manhasx`, `Mota`, `date`, `KhuyenMai`, `giakhuyenmai`) VALUES
(212, 'Điện thoại iPhone 12 Pro Max 128GB', '33.590', 'iphone-12-pro-max-xanh-duong-new-600x600-600x600.jpg', 18, '<p>Cứ mỗi năm, đến dạo cuối th&aacute;ng 8 v&agrave; gần đầu th&aacute;ng 9 th&igrave; mọi th&ocirc;ng tin s&ocirc;i sục mới về chiếc iPhone mới lại xuất hiện. Apple năm nay lại ra th&ecirc;m một chiếc iPhone mới với t&ecirc;n gọi mới l&agrave;&nbsp;<strong>iPhone 12 Pro Max</strong>, đ&acirc;y l&agrave; một d&ograve;ng điện thoại mới v&agrave; mạnh mẽ nhất của nh&agrave; Apple năm nay. Mời bạn tham khảo th&ecirc;m một số m&ocirc; tả sản phẩm b&ecirc;n dưới để bạn c&oacute; thể ra quyết định mua sắm</p>\r\n', '2021-01-02', 0, '0'),
(214, 'Điện thoại Xiaomi Mi 10T Pro 5G', '12.490', 'xiaomi-mi-10t-pro-den-600x600.jpg', 17, '<p bis_size=\"{&quot;x&quot;:20,&quot;y&quot;:20,&quot;w&quot;:1001,&quot;h&quot;:41,&quot;abs_x&quot;:481,&quot;abs_y&quot;:925}\">Mi 10T Pro 5G&nbsp;mẫu smartphone&nbsp;cao cấp của Xiaomi&nbsp;trong năm 2020 cuối c&ugrave;ng cũng được tr&igrave;nh l&agrave;ng với loạt những th&ocirc;ng số g&acirc;y &ldquo;cho&aacute;ng ngợp&rdquo;: m&agrave;n h&igrave;nh tần số qu&eacute;t 144 Hz, vi xử l&yacute; Snapdragon 865 v&agrave; cụm camera khủng 108 MP k&egrave;m theo đ&oacute; l&agrave; một mức gi&aacute; dễ chịu cho người d&ugrave;ng.</p>\r\n', '2021-01-02', 1, '10.490'),
(215, 'Điện thoại Xiaomi Mi Note 10 Lite', '9.990', 'xiaomi-mi-note-10-lite-tim-600x600.jpg', 17, '<p bis_size=\"{&quot;x&quot;:20,&quot;y&quot;:20,&quot;w&quot;:1001,&quot;h&quot;:41,&quot;abs_x&quot;:481,&quot;abs_y&quot;:925}\">Xiaomi Mi Note 10 Lite&nbsp;l&agrave;&nbsp;điện thoại th&ocirc;ng minh&nbsp;thế hệ Mi Note tiếp theo vừa được&nbsp;Xiaomi&nbsp;ch&iacute;nh thức ra mắt v&agrave;o th&aacute;ng 5/2020. M&aacute;y sở hữu nhiều trang bị hấp dẫn với bộ 4 camera l&ecirc;n đến 64 MP, thiết kế thời trang c&ugrave;ng m&agrave;n h&igrave;nh AMOLED cong 3D quyến rũ.</p>\r\n', '2021-01-02', 1, '8.990'),
(216, 'Điện thoại Samsung Galaxy Z Fold2 5G', '50.000', 'samsung-galaxy-z-fold-2-den-600x600.jpg', 16, '<h2>Đặc điểm nổi bật của Samsung Galaxy Z Fold2 5G</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/226099/Slider/vi-vn-samsung-galaxy-z-fold-2-tinhnang.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold-2\" target=\"_blank\">Samsung Galaxy Z Fold 2</a>&nbsp;l&agrave; t&ecirc;n gọi ch&iacute;nh thức của&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>&nbsp;m&agrave;n h&igrave;nh gập cao cấp nhất của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\">Samsung</a>.&nbsp;Với nhiều n&acirc;ng cấp ti&ecirc;n phong về thiết kế, hiệu năng v&agrave; camera, hứa hẹn đ&acirc;y sẽ l&agrave; một si&ecirc;u phẩm th&agrave;nh c&ocirc;ng tiếp theo đến từ &ldquo;&ocirc;ng tr&ugrave;m&rdquo; sản xuất điện thoại lớn nhất thế giới.</p>\r\n', '2021-01-02', 0, '0'),
(217, 'Điện thoại Samsung Galaxy Note 20 Ultra 5G', '32.990', 'sam-sung-note-20-ultra-vang-dong-600x600.jpg', 16, '<p>Đặc điểm nổi bật của Samsung Galaxy Note 20 Ultra 5G</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/-samsung-galaxy-note-20-ultra-5g-slider.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-thietke.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-manhinh.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-dynamic-amoled-2x.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-tan-so-quet.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-choi-game.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-s-pen.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-ghi-chu.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-camera-sau.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-chup-dem.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-laynet-dong.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-quay-8k.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-super-steady.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-zoom.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-pin.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-sac.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-samsung-notes.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-samsung-dex.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Slider/vi-vn-samsung-galaxy-note-20-ultra-5g-khang-nuoc.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p><img alt=\"Bộ sản phẩm chuẩn\" src=\"https://cdn.tgdd.vn/Products/Images/42/225063/Kit/samsung-galaxy-note-20-ultra-5g-bbh-org.jpg\" style=\"height:auto; margin:0px; width:780px\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Sạc, Tai nghe, S&aacute;ch hướng dẫn, B&uacute;t cảm ứng, C&aacute;p, C&acirc;y lấy sim</p>\r\n\r\n<p>Điện thoại Samsung Galaxy Note 20 Ultra 5G, m&acirc;̃u điện thoai&nbsp;flagship cao c&acirc;́p thu&ocirc;̣c dòng Note của Samsung, ra mắt th&aacute;ng 8/2020 với diện mạo thay đổi cùng những n&acirc;ng cấp đột ph&aacute;, đ&acirc;y chắc chắn sẽ là sản ph&acirc;̉m được săn l&ugrave;ng từ những người d&ugrave;ng y&ecirc;u thích c&ocirc;ng nghệ cũng như y&ecirc;u th&iacute;ch smartphone SamSung.</p>\r\n', '2021-01-02', 1, '26.990'),
(218, 'Điện thoại Huawei Nova 7i (Nền tảng Huawei Mobile Service)', '6.990', 'huawei-nova-7i-pink-600x600-1-600x600.jpg', 19, '<p>Sau th&agrave;nh c&ocirc;ng d&ograve;ng sản phẩm Nova 3i, Nova 5i,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\">Huawei</a>&nbsp;tiếp tục cho ra mắt người kế nhiệm mang t&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/huawei-nova-7i\" target=\"_blank\">Huawei Nova 7i</a>&nbsp;với nhiều đột ph&aacute; về cấu h&igrave;nh, thiết kế v&agrave; camera. Đi k&egrave;m với gi&aacute; th&agrave;nh phải chăng, thiết bi hứa hẹn tạo n&ecirc;n cơn sốt&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>&nbsp;cho năm 2020.</p>\r\n', '2021-01-02', 0, '0'),
(219, 'Tai nghe AirPods 2 Apple', '4.990', 'tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-avatar-1-600x600.jpg', 22, '<p>Giới thiệu sản phẩm</p>\r\n\r\n<p>Tai nghe AirPods 2 Apple&nbsp;vẫn giữ cho m&igrave;nh thiết kế hiện đại, cao cấp, hợp thời trang.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org1.jpg\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng thiết kế hiện đại, cao cấp\" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Tai nghe&nbsp;AirPods 2 trang bị chip H1 gi&uacute;p tai nghe kết nối ổn định, nhanh ch&oacute;ng v&agrave; tiết kiệm pin hơn</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org3.jpg\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng hỗ trợ chip H1 xử lý tốt hơn \" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org3.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Hỗ trợ k&iacute;ch hoạt Siri bằng giọng n&oacute;i &quot;Hey, Siri&quot; v&agrave; thao t&aacute;c cảm ứng tiện lợi</p>\r\n\r\n<p>Ngo&agrave;i ra, tai nghe&nbsp;AirPods 2 c&ograve;n hỗ trợ nhận cuộc gọi, c&oacute; thể nghe hoặc tạm dừng đoạn nhạc đang ph&aacute;t tr&ecirc;n điện thoại bằng những thao t&aacute;c chạm cảm ứng tr&ecirc;n tai nghe, tiện lợi cho người d&ugrave;ng khi di chuyển.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-2.jpg\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng kích hoạt Siri bằng giọng nói \" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-2.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Kết nối được hầu hết với c&aacute;c thiết bị Apple v&agrave; cả với c&aacute;c thiết bị hệ điều h&agrave;nh Android, Windows</p>\r\n\r\n<p>Tai nghe AirPods 2 sử dụng chuẩn kết nối Bluetooth được tối ưu cho mọi thiết bị của h&atilde;ng như iPhone, iPad, iMac, Macbook, Appe Watch v&agrave; cả những thiết bị điện thoại, m&aacute;y t&iacute;nh bảng&nbsp;chạy Android hay lap top, m&aacute;y t&iacute;nh Windows.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-23.jpg\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng kết nối với hầu hết các thiết bị Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-23.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Trang bị c&ocirc;ng nghệ khử tiếng ồn cuộc gọi, đảm bảo chất lượng cuộc đ&agrave;m thoại tốt nhất</p>\r\n\r\n<p>Tai nghe&nbsp;AirPods 2 trang bị t&iacute;nh năng khử ồn để tạo điểm nhấn kh&aacute;c biệt so với chiếc AirPods thế hệ đầu, nhằm cho ra chất &acirc;m tuyệt vời v&agrave; cải thiện khoảng c&aacute;ch kết nối giữa c&aacute;c thiết bị.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-25.jpg\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng khử tiếng ồn\" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-25.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Nghe nhạc l&ecirc;n đến 3 giờ cho mỗi một lần sạc nhanh 15 ph&uacute;t v&agrave; c&oacute; thể sử dụng hơn 24 giờ khi sạc nhiều lần với hộp sạc</p>\r\n\r\n<p>Điểm đặc biệt tr&ecirc;n tai nghe AirPods 2 Apple, sạc nhanh 15 ph&uacute;t sẽ c&oacute; thể nghe sạc đến 3 giờ (sử dụng ở mức &acirc;m lượng 50%) nhờ c&ocirc;ng nghệ sạc nhanh t&iacute;ch hợp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-15.jpg\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng sử dụng pin dài lâu\" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-15.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n', '2021-01-02', 1, '3.990'),
(220, 'Tai nghe AirPods Pro sạc không dây Apple', '6.990', 'tai-nghe-bluetooth-airpods-pro-apple-mwp22-ava-600x600.jpg', 22, '<p>Thiết kế nhỏ gọn, dễ d&agrave;ng đem theo bất cứ đ&acirc;u</p>\r\n\r\n<p>AirPods Pro&nbsp;với thiết kế gọn g&agrave;ng, đẹp v&agrave; tinh tế,&nbsp;tai nghe&nbsp;được thiết kế theo dạng&nbsp;In-ear thay v&igrave; Earbuds như&nbsp;AirPods 2, cho ph&eacute;p&nbsp;<strong>chống ồn tốt hơn, kh&oacute; rớt khi đeo v&agrave; &ecirc;m tai hơn</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22.jpg\"><img alt=\"Thiết kế nhỏ gọn, tinh tế - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Kết nối nhanh v&agrave; ổn định với c&ocirc;ng nghệ Bletooth 5.0</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-bluetooth\" target=\"_blank\">Tai nghe Bluetooth</a>&nbsp;AirPods Pro vẫn cho&nbsp;tốc độ kết nối ấn tượng, chỉ&nbsp;<strong>khoảng&nbsp;1 - 2 gi&acirc;y</strong>&nbsp;sau khi mở nắp (với c&aacute;c thiết bị của Apple như iPhone, iPad, MacBook,...) v&agrave;&nbsp;<strong>khoảng 3 - 4 gi&acirc;y</strong>&nbsp;đối với Android (sau lần kết nối đầu ti&ecirc;n).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang2.jpg\"><img alt=\"Kết nối nhanh chóng - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang2.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Linh hoạt lựa chọn với 3 k&iacute;ch cỡ đệm tai k&egrave;m theo</p>\r\n\r\n<p>Do chuyển sang dạng In-ear, n&ecirc;n AirPods Pro được&nbsp;<strong>k&egrave;m theo đệm tai cao su với 3 k&iacute;ch cỡ kh&aacute;c nhau</strong>, gi&uacute;p người d&ugrave;ng c&oacute; nhiều lựa chọn ph&ugrave; hợp với cỡ tai của m&igrave;nh, đem lại trải nghiệm thoải m&aacute;i nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang3.jpg\"><img alt=\"Đi kèm theo 3 cặp đệm tai - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang3.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>&Acirc;m thanh chất lượng, sống động c&ugrave;ng c&ocirc;ng nghệ Adaptive EQ tự&nbsp;điều chỉnh &acirc;m thanh th&ocirc;ng minh</p>\r\n\r\n<p>AirPods Pro được n&acirc;ng cấp mạnh mẽ ở chất lượng &acirc;m thanh, cho &acirc;m bass mạnh mẽ hơn, ch&acirc;n thực hơn, độ chi tiết cao hơn, c&ugrave;ng c&ocirc;ng nghệ Adaptive EQ, cho ph&eacute;p<strong>&nbsp;</strong>tự động điều chỉnh tần số &acirc;m thanh&nbsp;của bản nhạc theo h&igrave;nh dạng tai người d&ugrave;ng, mang lại trải nghiệm &acirc;m thanh tốt nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trangfixx4.jpg\"><img alt=\"Công nghệ Adaptive EQ tùy chỉnh âm tùy theo người dùng - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trangfixx4.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Tai nghe&nbsp;AirPods Pro trang bị 2 chế độ: Transparency Mode (xuy&ecirc;n &acirc;m) v&agrave; Noise Cancellation Mode (chống ồn chủ động)</p>\r\n\r\n<p><strong>- Chống ồn chủ động:</strong>&nbsp;Với chế độ n&agrave;y, tai nghe sẽ khử tiếng ồn ngo&agrave;i m&ocirc;i trường, loại bỏ tạp &acirc;m xung quanh lọt v&agrave;o tai, gi&uacute;p bạn đắm ch&igrave;m v&agrave;o bản nhạc m&agrave; kh&ocirc;ng bị ảnh hưởng bởi tiếng ồn xung quanh.</p>\r\n\r\n<p><strong>- Chế độ xuy&ecirc;n &acirc;m:&nbsp;</strong>Khi chuyển sang chế độ n&agrave;y, AirPods Pro cho ph&eacute;p bạn nghe được cả nhạc đang ph&aacute;t v&agrave; &acirc;m thanh b&ecirc;n ngo&agrave;i, gi&uacute;p bạn dễ d&agrave;ng hơn khi tr&ograve; chuyện với mọi người d&ugrave; đang đeo tai nghe.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-6.jpg\"><img alt=\"Trang bị chống ồn và chế độ xuyên âm - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-6.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Điều khiển dễ d&agrave;ng, nhanh ch&oacute;ng với cảm ứng lực hiện đại</p>\r\n\r\n<p>Tai nghe AirPods Pro&nbsp;sử dụng cảm ứng lực để điều khiển,&nbsp;kh&ocirc;ng cần thao t&aacute;c nhiều tr&ecirc;n điện thoại, cụ thể như sau:</p>\r\n\r\n<p><strong>- B&oacute;p 1 lần:</strong>&nbsp;Ph&aacute;t/tạm dừng nhạc hoặc trả lời cuộc gọi.</p>\r\n\r\n<p><strong>- B&oacute;p 2 lần:</strong>&nbsp;Chuyển b&agrave;i kế tiếp.</p>\r\n\r\n<p><strong>- B&oacute;p 3 lần:</strong>&nbsp;Quay lại b&agrave;i trước.</p>\r\n\r\n<p><strong>- Nhấn giữ:</strong>&nbsp;Chuyển đổi giữa chế độ chống ồn chủ động v&agrave; chế độ xuy&ecirc;n &acirc;m.</p>\r\n\r\n<p>Ngo&agrave;i ra, AirPods Pro c&ograve;n hỗ trợ kết nối nhanh với trợ l&yacute; ảo bằng c&aacute;ch n&oacute;i&nbsp;<strong>&quot;Hey Siri&quot;</strong>, sau đ&oacute; bạn c&oacute; thể ra lệnh ph&aacute;t b&agrave;i h&aacute;t, thực hiện cuộc gọi, nhờ dẫn đường v&agrave; c&aacute;c y&ecirc;u cầu kh&aacute;c (chỉ hỗ trợ giọng n&oacute;i Tiếng Anh).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-7.jpg\"><img alt=\"Cảm ứng lực dễ dàng thao tác, hiện đại - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-7.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>T&iacute;ch hợp sạc kh&ocirc;ng d&acirc;y chuẩn Qi cho hộp sạc</p>\r\n\r\n<p>Hộp sạc AirPods Pro hỗ trợ sạc kh&ocirc;ng d&acirc;y&nbsp;theo chuẩn Qi, tương th&iacute;ch hầu hết c&aacute;c loại đế sạc kh&ocirc;ng d&acirc;y hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang7.jpg\"><img alt=\"Hỗ trợ sạc không dây chuẩn Qi - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang7.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, bạn c&oacute; thể sạc cho hộp sạc qua cổng Lightning, đi k&egrave;m theo bộ sản phẩm l&agrave;&nbsp;c&aacute;p USB-C To Lightning&nbsp;giống với c&aacute;p sạc đi k&egrave;m với&nbsp;iPhone 11 Pro/11 Pro Max.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang8.jpg\"><img alt=\"Hỗ trợ sạc qua cổng Lightning - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang8.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Thời lượng pin cho ph&eacute;p nghe nhạc đến 4.5 giờ, đ&agrave;m thoại đến 3.5 giờ</p>\r\n\r\n<p>Khi bật chức năng khử tiếng ồn chủ động, thời gian nghe nhạc đến 4.5 giờ v&agrave; thời gian đ&agrave;m thoại đến 3.5 giờ li&ecirc;n tục nếu bật chống ồn, c&ograve;n&nbsp;nếu tắt chống ồn, c&oacute; thể nghe nhạc đến 5 giờ v&agrave; đ&agrave;m thoại đến 4.5 giờ.</p>\r\n\r\n<p>Khi d&ugrave;ng tai nghe AirPods Pro với hộp sạc th&igrave; người d&ugrave;ng sẽ c&oacute; thời gian&nbsp;<strong>24 giờ nghe nhạc v&agrave; 18 giờ đ&agrave;m thoại</strong>&nbsp;li&ecirc;n tục khi được sạc đầy.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang9.jpg\"><img alt=\"Có thể nghe nhạc liên tục trong 4.5 giờ và 3.5 giờ đàm thoại - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang9.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Tiết kiệm thời gian với khả năng sạc nhanh</p>\r\n\r\n<p>Với t&iacute;nh năng&nbsp;<strong>sạc 5 ph&uacute;t được 1 giờ nghe nhạc</strong>, AirPods Pro gi&uacute;p cho bạn tiết kiệm thời gian v&agrave; an t&acirc;m hơn mỗi khi tai nghe hết pin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang10.jpg\"><img alt=\"Hỗ trợ sạc nhanh, 5 phút cho 1 giờ nghe nhạc - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang10.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Khả năng kh&aacute;ng nước v&agrave; mồ h&ocirc;i với chuẩn chống nước IPX4</p>\r\n\r\n<p>AirPods Pro c&oacute; thể chống được mưa nhỏ v&agrave; mồ h&ocirc;i khi vận động, gi&uacute;p người d&ugrave;ng y&ecirc;n t&acirc;m v&agrave; thoải m&aacute;i hơn khi đeo để chơi thể thao hoặc c&oacute; v&ocirc; t&igrave;nh gặp mưa (kh&ocirc;ng n&ecirc;n đeo khi bơi hoặc ng&acirc;m nước).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang11.jpg\"><img alt=\"Hỗ trợ chống nước và mồ hôi chuẩn IPX4 - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang11.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Dễ d&agrave;ng theo d&otilde;i trạng th&aacute;i pin th&ocirc;ng qua đ&egrave;n LED</p>\r\n\r\n<p>Đ&egrave;n LED được đặt ở vị tr&iacute; ngay mặt trước hộp sạc để người d&ugrave;ng tiện theo d&otilde;i trạng th&aacute;i pin, cụ thể đ&egrave;n xanh b&aacute;o hiệu pin đầy v&agrave; đ&egrave;n cam l&agrave; pin yếu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-8.jpg\"><img alt=\"Trang bị đèn LED báo pin tiện lợi - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-8.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Bộ sản phẩm gồm c&oacute;:</p>\r\n\r\n<p>Hộp sạc, tai nghe (b&ecirc;n trong hộp sạc), d&acirc;y c&aacute;p USB-C To Lightning, 3 cặp đệm tai Silicon (2 cặp rời, 1 cặp đi k&egrave;m sẵn trong tai nghe), s&aacute;ch hướng dẫn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang12.jpg\"><img alt=\"Bộ sản phẩm bao gồm Tai nghe, Hộp sạc, USB-C to Lightning, đệm tai Silicon (3 cỡ), sách hướng dẫn\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang12.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Hướng dẫn kết nối iPhone/iPad với&nbsp;AirPods Pro:</p>\r\n\r\n<p>- Bật Bluetooth tr&ecirc;n iPhone/iPad.</p>\r\n\r\n<p>- Mở nắp hộp&nbsp;AirPods Pro, giữ nguy&ecirc;n tai nghe trong hộp, l&uacute;c n&agrave;y tr&ecirc;n thiết bị sẽ xuất hiện 1 bảng pop-up c&oacute; h&igrave;nh tai nghe v&agrave; chữ Connect (kết nối).</p>\r\n\r\n<p>- Bấm Connect sau n&oacute; nhấn giữ n&uacute;t nhấn ph&iacute;a mặt sau hộp sạc, đợi v&agrave;i gi&acirc;y l&agrave; ho&agrave;n th&agrave;nh việc kết nối. Những lần sau chỉ cần mở nắp hộp, tai nghe sẽ tự động kết nối với thiết bị.</p>\r\n\r\n<p>Với MacOS (MacBook/iMac), iPhone c&aacute;ch kết nối cũng rất dễ d&agrave;ng v&agrave; tương th&iacute;ch tốt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang15.jpg\"><img alt=\"Hướng dẫn kết nối Tai nghe Bluetooth Airpods Pro Apple Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang15.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung, với thiết kế nhỏ gọn,<strong>&nbsp;</strong>linh hoạt v&agrave; hợp thời trang,&nbsp;tai nghe Apple&nbsp;AirPods Pro sẽ mang đến cho người d&ugrave;ng những trải nghiệm &acirc;m thanh tốt nhất, chế độ chống ồn chủ động v&agrave; chế độ xuy&ecirc;n &acirc;m hoạt động cực k&igrave; hiệu quả, mang lại trải nghiệm tuyệt vời chất lượng &acirc;m thanh ch&acirc;n thực.</p>\r\n', '2021-01-02', 1, '5.990'),
(221, 'Tai nghe chụp tai Bluetooth Sony WH-1000XM4', '8.490', 'tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-ava-600x600.jpg', 21, '<p>Thiết kế trẻ trung, gọn nhẹ</p>\r\n\r\n<p>Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen&nbsp;c&oacute; thiết kế đơn giản, k&iacute;ch thước nhỏ gọn, m&agrave;u sắc h&agrave;i h&ograve;a đ&aacute;p ứng nhu cầu giải tr&iacute; bằng &acirc;m nhạc ở mọi địa điểm. Tai nghe c&oacute; cấu tr&uacute;c gấp th&ocirc;ng minh bạn c&oacute; thể dễ d&agrave;ng gấp bỏ v&agrave;o balo mang theo mu&ocirc;n nơi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-075420-085449.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-075420-085449.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Kết nối kh&ocirc;ng d&acirc;y đơn giản nhanh ch&oacute;ng th&ocirc;ng qua Bluetooth.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-075520-085559.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Bluetooth \" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-075520-085559.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Chụp tai được trang bị lớp đệm bằng uretan dạng bọt gi&uacute;p giảm &aacute;p lực l&ecirc;n tai, ph&acirc;n phối đều &aacute;p lực v&agrave; tăng khả năng b&aacute;m sao cho vừa kh&iacute;t chắc chắn. Khi bạn đeo cả ng&agrave;y m&agrave; vẫn cực thoải m&aacute;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-075520-085534.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - đệm tai nghe \" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-075520-085534.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Thưởng thức &acirc;m nhạc trọn vẹn với c&ocirc;ng nghệ chống ồn HD QN1&nbsp;</p>\r\n\r\n<p>Tai nghe Sony n&agrave;y được trang bị c&ocirc;ng nghệ chống ồn HD QN1, c&ocirc;ng nghệ n&agrave;y sẽ xử l&yacute; tiếng ồn thu được từ bộ thu tiếng ồn k&eacute;p bằng c&aacute;c thuật to&aacute;n mới, gi&uacute;p loại bỏ tạp &acirc;m cho bạn đắm ch&igrave;m với thế giới &acirc;m nhạc tuyệt vời.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-075920-085937.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Chống ồn\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-075920-085937.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>&Acirc;m trầm s&acirc;u lắng, &acirc;m thanh ch&acirc;n thực</p>\r\n\r\n<p>Tai nghe&nbsp;được trang bị m&agrave;ng loa c&oacute; k&iacute;ch thước 40 mm kết hợp c&ugrave;ng m&agrave;n chắn Polymer tinh thể lỏng (LCD) gi&uacute;p xử l&yacute; ho&agrave;n hảo những nốt trầm s&acirc;u v&agrave; t&aacute;i tạo được to&agrave;n bộ dải tần số &acirc;m thanh l&ecirc;n 40 kHz. B&ecirc;n cạnh đ&oacute; bộ khuếch đại &acirc;m thanh analog được t&iacute;ch hợp trong bộ xử l&yacute; chống ồn cho &acirc;m thanh th&ecirc;m phần vang dội v&agrave; mạnh mẽ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070320-090334.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Màng loa 40 mm\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070320-090334.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Tự động ngừng ph&aacute;t nhạc khi bạn tr&ograve; chuyện</p>\r\n\r\n<p>Khi bạn n&oacute;i chuyện với ai đ&oacute; khi đang sử dụng tai nghe th&igrave; t&iacute;nh năng N&oacute;i để tr&ograve; chuyện sẽ dừng ph&aacute;t nhạc v&agrave; tắt chế độ chống ồn để &acirc;m thanh b&ecirc;n ngo&agrave;i lọt v&agrave;o gi&uacute;p bạn tập chung v&agrave;o c&acirc;u chuyện của m&igrave;nh hơn, khi bạn kết th&uacute;c cuộc tr&ograve; chuyển th&igrave; &acirc;m nhạc sẽ được ph&aacute;t lại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070420-090448.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Tự ngừng phát nhạc khi trò chuyện\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070420-090448.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Tự động ngừng ph&aacute;t nhạc khi bạn th&aacute;o tai nghe ra</p>\r\n\r\n<p>Cảm biến tiệm cận v&agrave; hai cảm biến gia tốc b&ecirc;n trong tai nghe sẽ ph&aacute;t hiện xem bạn c&oacute; đang đeo tai nghe hay kh&ocirc;ng, khi bạn kh&ocirc;ng đeo tai nghe th&igrave; sẽ tự động ngừng ph&aacute;t, khi bạn đeo tai nghe trở lại th&igrave; sẽ tự động điều chỉnh ph&aacute;t lại cho ph&ugrave; hợp để gi&uacute;p tiết kiệm pin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070520-090546.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Cảm biến tiệm cận\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070520-090546.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Tiến v&agrave;o cuộc tr&ograve; chuyện nhanh ch&oacute;ng</p>\r\n\r\n<p>Khi bất ngờ bạn c&oacute; cuộc tr&ograve; chuyện bất ngờ v&agrave; bạn cần hạ thấp &acirc;m thanh của loa xuống để lắng nghe giọng n&oacute;i xung quanh th&igrave; bạn chỉ cần đặt tay l&ecirc;n vỏ tai nghe để hạ &acirc;m lượng tức th&igrave; v&agrave; cho &acirc;m thanh b&ecirc;n ngo&agrave;i lọt v&agrave;o.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070720-090754.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Chạm để giảm âm lượng\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070720-090754.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Thỏa sức nghe nhạc cả ng&agrave;y d&agrave;i với thời lượng pin khủng v&agrave; sạc nhanh</p>\r\n\r\n<p>Tai nghe Sony&nbsp;với thời lượng pin cực khủng l&ecirc;n đến 30 giờ đ&aacute;p ứng nhu cầu nghe nhạc cả ng&agrave;y d&agrave;i của bạn. Nếu bạn muốn trải nghiệm sạc nhanh chỉ với 10 ph&uacute;t sạc &agrave; đ&atilde; c&oacute; 5 giờ nghe nhạc th&igrave; bạn chỉ cần sử dụng bộ chuyển đổi AC được mua ri&ecirc;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070920-090905.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Pin khủng\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070920-090905.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Kiểm so&aacute;t việc vận h&agrave;nh của tai nghe một c&aacute;ch nhanh ch&oacute;ng v&agrave; đơn giản</p>\r\n\r\n<p>Trợ l&yacute; Google Assistant bạn c&oacute; thể thoải m&aacute;i giải tr&iacute;, kết nối với bạn b&egrave;, xem th&ocirc;ng tin, nghe nhạc v&agrave; th&ocirc;ng b&aacute;o, đặt lời nhắc v&agrave; nhiều hoạt động kh&aacute;c chỉ th&ocirc;ng qua việc ra lệnh bằng giọng n&oacute;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070920-090948.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Google Assistant\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-070920-090948.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Giờ đ&acirc;y bạn kh&ocirc;ng cần phải th&aacute;o tai nghe ra v&agrave; nhấc điện thoại l&ecirc;n để nhận cuộc gọi nữa, bạn chỉ cần chạm nhẹ 2 lần l&ecirc;n tai nghe để n&oacute;i. Nhờ C&ocirc;ng nghệ thu giọng n&oacute;i ch&iacute;nh x&aacute;c kết hợp với micro t&iacute;ch hợp WH-1000XM4 sẽ truyền &acirc;m thanh cực r&otilde; n&eacute;t tới người b&ecirc;n kia đầu d&acirc;y.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071020-091059.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Gọi rảnh tay\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071020-091059.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Thay đổi b&agrave;i h&aacute;t, tăng hoặc giảm &acirc;m lượng, thực hiện hoặc nhận cuộc gọi chỉ bằng thao t&aacute;c chạm hoặc vuốt bảng điều khiển.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071020-091053.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Điều khiển\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071020-091053.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Kết nối c&ugrave;ng l&uacute;c 2 thiết bị</p>\r\n\r\n<p>Tai nghe Bluetooth&nbsp;c&oacute; thể gh&eacute;p nối với hai thiết bị Bluetooth c&ugrave;ng l&uacute;c. Khi c&oacute; cuộc gọi đến, tai nghe của bạn sẽ tự nhận biết thiết bị n&agrave;o đang đổ chu&ocirc;ng v&agrave; tự động kết nối với thiết bị đ&oacute;. Bạn cũng c&oacute; thể nhanh ch&oacute;ng chuyển tai nghe của m&igrave;nh sang một trong hai thiết bị dễ d&agrave;ng chỉ với một thao t&aacute;c chạm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071220-091213.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Kết nối 2 thiết bị\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071220-091213.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>C&aacute;c cổng v&agrave; n&uacute;t tr&ecirc;n tai nghe</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071420-091449.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Nút điều khiển\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071420-091449.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071720-091732.jpg\"><img alt=\"Tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen - Cổng kết nối\" src=\"https://cdn.tgdd.vn/Products/Images/54/227725/tai-nghe-chup-tai-bluetooth-sony-wh-1000xm4-den-071720-091732.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>C&oacute; thể thấy, tai nghe chụp tai Bluetooth Sony WH-1000XM4/BME Đen với thiết kế gọn nhẹ, nhiều c&ocirc;ng nghệ &acirc;m thanh đỉnh cao sẽ đem đến cho bạn những ph&uacute;t gi&acirc;y giải tr&iacute; tuyệt vời.</p>\r\n', '2021-01-02', 0, '0'),
(222, 'Điện thoại Vsmart Aris Pro', '8.990', 'vsmart-aris-pro-green-600jpg-600x600.jpg', 20, '<p>Thương hiệu&nbsp;smartphone&nbsp;Việt -&nbsp;Vsmart&nbsp;đ&atilde; ch&iacute;nh thức tr&igrave;nh l&agrave;ng&nbsp;Vsmart Aris Pro. Sự ra mắt mẫu điện thoại n&agrave;y đ&aacute;nh dấu một bước ngoặc lớn trong l&agrave;ng smartphone với c&ocirc;ng nghệ camera ẩn trong m&agrave;n h&igrave;nh, mức gi&aacute; hợp l&yacute; gi&uacute;p người Việt c&oacute; cơ hội tiếp cận những c&ocirc;ng nghệ mới nhất hiện nay.</p>\r\n\r\n<p>Camera selfie ẩn, đỉnh cao của thiết kế m&agrave;n h&igrave;nh tr&agrave;n viền</p>\r\n\r\n<p>Aris Pro l&agrave; một trong những d&ograve;ng điện thoại đầu ti&ecirc;n được trang bị camera selfie với c&ocirc;ng nghệ ẩn sau m&agrave;n h&igrave;nh độc đ&aacute;o, tạo n&ecirc;n một m&agrave;n h&igrave;nh liền mạch, kh&ocirc;ng c&ograve;n khiếm khuyết &ldquo;đục lỗ&rdquo; hay &ldquo;giọt nước&rdquo;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-282420-082418.jpg\"><img alt=\"Màn hình vô khuyết chứa camera ẩn dưới màn hình - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-282420-082418.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Với ưu điểm mang t&iacute;nh đột ph&aacute;, camera ẩn sẽ l&agrave; xu thế c&ocirc;ng nghệ kế tiếp tr&ecirc;n c&aacute;c d&ograve;ng smartphone trong những năm sắp tới. Vsmart cũng sẽ l&agrave; h&atilde;ng điện thoại Việt Nam đầu ti&ecirc;n ti&ecirc;n phong đưa c&ocirc;ng nghệ n&agrave;y l&ecirc;n smartphone của m&igrave;nh sớm nhất.</p>\r\n\r\n<p>Camera ẩn sau m&agrave;n h&igrave;nh (Camera Under Display - CUD) xử l&yacute; h&igrave;nh ảnh bằng tr&iacute; tuệ nh&acirc;n tạo d&agrave;nh ri&ecirc;ng cho camera ẩn, gi&uacute;p ph&acirc;n t&iacute;ch nguồn s&aacute;ng tr&ecirc;n m&agrave;n h&igrave;nh loại bỏ hiện tượng ảnh mờ, thiếu, nhiễu, m&agrave;u sắc sai lệch đi qua lớp k&iacute;nh d&agrave;y, t&aacute;i tạo c&aacute;c chi tiết, m&agrave;u sắc để đưa lại chất lượng ảnh về đ&uacute;ng ban đầu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-282520-082520.jpg\"><img alt=\"Màn hình tràn viền sắc nét và chi tiết - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-282520-082520.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Ở chế độ sử dụng th&ocirc;ng thường, m&agrave;n h&igrave;nh Aris Pro&nbsp;tr&agrave;n viền&nbsp;ho&agrave;n hảo kh&ocirc;ng t&igrave; vết. Khi bắt đầu sử dụng camera trước, phần m&agrave;n h&igrave;nh ph&iacute;a tr&ecirc;n tại nơi đặt cảm biến lớp k&iacute;nh sẽ mờ đi để &aacute;nh s&aacute;ng c&oacute; thể lọt v&agrave;o cảm biến.</p>\r\n\r\n<p>Thuật to&aacute;n AI n&agrave;y sẽ được li&ecirc;n tục được h&atilde;ng cải tiến n&acirc;ng cấp nhằm c&oacute; thể nhận diện ch&iacute;nh x&aacute;c n&acirc;ng cao chất lượng h&igrave;nh ảnh trong nhiều điều kiện chụp phức tạp, người d&ugrave;ng sẽ nhận được c&aacute;c bản n&acirc;ng cấp trong suốt qu&aacute; tr&igrave;nh sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-214220-034235.jpg\"><img alt=\"Các thuật toán AI luôn được cải tiến và nâng cấp - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-214220-034235.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Hiệu năng mạnh mẽ với mức gi&aacute; tầm trung</p>\r\n\r\n<p>Aris Pro cũng l&agrave; chiếc điện thoại mạnh mẽ nhất của Vsmart ở thời điểm hiện tại (9/2020) khi trang bị vi xử l&yacute; Snapdragon 730 kết hợp c&ugrave;ng&nbsp;RAM 8 GB&nbsp;mạnh mẽ cho hiệu năng tiệm cận với d&ograve;ng cao cấp, với sức mạnh như vậy gi&uacute;p&nbsp;điện thoại chơi game&nbsp;c&oacute; đồ họa phức tạp ho&agrave;n to&agrave;n kh&ocirc;ng thể l&agrave;m kh&oacute; được.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-214420-034458.jpg\"><img alt=\"Hiệu năng mạnh mẽ, vượt trội - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-214420-034458.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, bộ nhớ 128 GB lu&ocirc;n đủ kh&ocirc;ng gian để lưu trữ được cả kho nhạc, phim chất lượng cao, cho bạn c&oacute; thể giải tr&iacute; bất cứ l&uacute;c n&agrave;o m&agrave; kh&ocirc;ng cần phải lo lắng về vấn đề lưu trữ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-282820-082817.jpg\"><img alt=\"Khe cắm sim trên điện thoại - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-282820-082817.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>T&iacute;ch hợp vi&ecirc;n pin 4000 mAh b&ecirc;n trong gi&uacute;p cho mẫu điện thoại mới của Vsmart c&oacute; thời gian sử dụng trong khoảng 1 ng&agrave;y. B&ecirc;n cạnh đ&oacute;, m&aacute;y cũng sẽ đi k&egrave;m 1 củ&nbsp;sạc pin nhanh&nbsp;18 W cho tốc độ sạc v&ocirc; c&ugrave;ng nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-282720-082705.jpg\"><img alt=\"Pin 4000 mAh tích hợp sạc nhanh 18W - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-282720-082705.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Ch&uacute; trọng đến chăm s&oacute;c sức khỏe m&ugrave;a dịch</p>\r\n\r\n<p>Một điểm đặc biệt nhưng cũng kh&aacute; quan trọng trong giai đoạn n&agrave;y đ&oacute; l&agrave; đi k&egrave;m mỗi chiếc Aris Pro l&agrave; một miếng d&aacute;n m&agrave;n h&igrave;nh đặc biệt, c&oacute; khả năng ph&ograve;ng chống đến&nbsp;99,7% virus l&acirc;y nhiễm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-214920-034946.jpg\"><img alt=\"Màn hình có khả năng chống nhiễm khuẩn - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-214920-034946.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng chỉ vậy, Aris Pro cũng l&agrave; d&ograve;ng m&aacute;y đầu ti&ecirc;n của Vsmart t&iacute;ch hợp ứng dụng sức khoẻ AI Dr. AidTM do VinBrain (thuộc Vingroup) ph&aacute;t triển để hỗ trợ chẩn đo&aacute;n c&aacute;c bệnh l&yacute; về tim phổi, xương dựa tr&ecirc;n c&aacute;c ảnh chụp xquang một c&aacute;ch chuẩn x&aacute;c, gi&uacute;p người bệnh theo d&otilde;i v&agrave; tự kiểm so&aacute;t bệnh tại nh&agrave;.</p>\r\n\r\n<p>Bộ tứ camera đỉnh cao, mu&ocirc;n v&agrave;n g&oacute;c chụp chất lượng</p>\r\n\r\n<p>Mặt lưng của Aris Pro nổi bật với cụm camera vu&ocirc;ng độc đ&aacute;o chứa 4 camera, được đặt ngay ch&iacute;nh giữa gần cạnh tr&ecirc;n, b&ecirc;n dưới l&agrave; dải đ&egrave;n flash k&eacute;p trợ s&aacute;ng. Kiểu thiết kế kh&aacute; giống với&nbsp;Redmi Note 9 Pro&nbsp;của nh&agrave;&nbsp;Xiaomi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-283120-083107.jpg\"><img alt=\"Hệ thống camera sau - Vsmart Ảris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-283120-083107.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Về th&ocirc;ng số, Vsmart Aris Pro kh&ocirc;ng thua k&eacute;m bất kỳ mẫu flagship n&agrave;o khi trang bị d&agrave;n cảm biến với th&ocirc;ng số khủng: camera ch&iacute;nh 64 MP, camera&nbsp;g&oacute;c rộng&nbsp;8 MP, camera zoom 2x 8 MP, v&agrave; camera chụp cận macro 2 MP.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-282220-082259.jpg\"><img alt=\"Cụm 4 camera sau chất lượng, đa tính năng - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-282220-082259.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>T&iacute;ch hợp AI cho ph&eacute;p camera tr&ecirc;n Vsmart Aris nhận diện, ph&acirc;n t&iacute;ch m&ocirc;i trường chụp từ đ&oacute; d&ugrave;ng thuật to&aacute;n AI c&acirc;n chỉnh lại c&aacute;c th&ocirc;ng số độ s&aacute;ng, iso, khẩu độ, m&agrave;u sắc để cho ra bức ảnh tốt nhất chỉ với 1 thao t&aacute;c bấm chụp đơn giản, thay v&igrave; chỉnh h&agrave;ng t&aacute; c&aacute;c th&ocirc;ng số phức tạp như trước kia.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-283220-083234.jpg\"><img alt=\"Tích hợp AI cho ra tấm ảnh đẹp hơn - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-283220-083234.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, camera g&oacute;c rộng với g&oacute;c nh&igrave;n tương đương với mắt người, gi&uacute;p bạn thu được to&agrave;n bộ những cảnh đẹp trước v&agrave;o khung h&igrave;nh dễ d&agrave;ng, hiệu ứng g&oacute;c rộng gi&uacute;p h&igrave;nh ảnh phong cảnh của bạn trở n&ecirc;n rộng lớn h&ugrave;ng vĩ hơn bao giờ hết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-215420-035416.jpg\"><img alt=\"Camera góc rộng 8 MP dễ dàng bắt trọn mọi chi tiết trên khung hình - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-215420-035416.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Chưa dừng lại ở đ&oacute;, bạn c&oacute; thể chụp những sự vật khoảng c&aacute;ch xa nhờ v&agrave;o&nbsp;camera zoom&nbsp;2x. Ngược lại, cảm biến&nbsp;chụp cận macro&nbsp;gi&uacute;p bạn c&oacute; được những tấm h&igrave;nh cận cảnh ấn tượng, đầy m&ecirc; hoặc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-214720-034758.jpg\"><img alt=\"Camera macro 2 MP chụp cận cảnh độc đáo - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-214720-034758.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>VOS bộ giao diện người d&ugrave;ng d&agrave;nh ri&ecirc;ng cho người Việt</p>\r\n\r\n<p>Aris Pro cũng sẽ chạy tr&ecirc;n VOS độc quyền từ Vsmart. VOS chạy&nbsp;Android&nbsp;thuần &iacute;t t&ugrave;y biến, đồng thời giao diện th&acirc;n thiện, hỗ trợ tiếng Việt sẽ l&agrave; một điểm cộng, gi&uacute;p tối ưu hiệu năng, m&aacute;y chạy mượt m&agrave; v&agrave; th&acirc;n thiện cho người d&ugrave;ng Việt.</p>\r\n\r\n<p>Aris Pro cũng l&agrave; một trong số &iacute;t điện thoại hiện nay c&oacute; &aacute;p dụng c&ocirc;ng nghệ bảo mật lượng tử Quantum QRNG v&agrave; c&ocirc;ng nghệ kh&oacute;a x&aacute;c thực FIDO2 ti&ecirc;n tiến n&acirc;ng mức bảo mật cao nhất, bảo vệ t&agrave;i khoản ng&acirc;n h&agrave;ng, th&ocirc;ng tin quan trọng của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228531/vsmart-aris-pro-215620-035638.jpg\"><img alt=\"Công nghệ bảo mật lượng tử Quantum QRNG - Vsmart Aris Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/228531/vsmart-aris-pro-215620-035638.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>C&oacute; thể thấy, với điểm s&aacute;ng l&agrave; ti&ecirc;n phong trong c&ocirc;ng nghệ camera ẩn sau m&agrave;n h&igrave;nh. Vsmart Aris hứa hẹn tạo n&ecirc;n cơn sốt trong giới c&ocirc;ng nghệ, mở ra một xu hướng mới về m&agrave;n h&igrave;nh tr&agrave;n viền kh&ocirc;ng khiếm khuyết trong tương lai kh&ocirc;ng xa.</p>\r\n', '2021-01-02', 0, '0');
INSERT INTO `sanpham` (`ID`, `Ten`, `Gia`, `HinhAnh`, `Manhasx`, `Mota`, `date`, `KhuyenMai`, `giakhuyenmai`) VALUES
(223, 'Điện thoại iPhone 11 128GB', '21.990', 'iphone-11-128gb-(8).jpg', 18, '<p>Được xem l&agrave; phi&ecirc;n bản iPhone &quot;gi&aacute; rẻ&quot; trong bộ 3 iPhone mới ra mắt nhưng&nbsp;iPhone 11 128GB&nbsp;vẫn sở hữu cho m&igrave;nh rất nhiều ưu điểm m&agrave; hiếm c&oacute; một chiếc&nbsp;smartphone&nbsp;n&agrave;o kh&aacute;c sở hữu.</p>\r\n\r\n<p>N&acirc;ng cấp mạnh mẽ về cụm camera</p>\r\n\r\n<p>Năm nay với iPhone 11 th&igrave; Apple đ&atilde; n&acirc;ng cấp kh&aacute; nhiều về camera nếu so s&aacute;nh với chiếc&nbsp;iPhone Xr 128GB&nbsp;năm ngo&aacute;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd7-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Cụm camera kép phía sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd7-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Ch&uacute;ng ta đ&atilde; c&oacute; bộ đ&ocirc;i&nbsp;camera k&eacute;p&nbsp;thay v&igrave; camera đơn như tr&ecirc;n thế hệ cũ v&agrave; với một camera g&oacute;c si&ecirc;u rộng th&igrave; bạn cũng c&oacute; nhiều hơn những lựa chọn khi chụp h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-1-2.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Giao diện chụp ảnh\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-1-2.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Trước đ&acirc;y để lấy được hết kiến tr&uacute;c của một t&ograve;a nh&agrave;, để ghi lại hết sự h&ugrave;ng vĩ của một ngọn n&uacute;i th&igrave; kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c l&agrave; bạn phải di chuyển ra kh&aacute; xa để chụp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd1-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Ảnh chụp góc siêu rộng\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd1-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p><em>Ảnh chụp chế độ g&oacute;c si&ecirc;u rộng</em></p>\r\n\r\n<p>Nhưng với&nbsp;g&oacute;c si&ecirc;u rộng&nbsp;tr&ecirc;n iPhone 11 th&igrave; c&oacute; thể cho bạn những bức ảnh với hiệu ứng g&oacute;c rộng rất ấn tượng v&agrave; th&iacute;ch mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd7.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Ảnh chụp chế độ chân dung\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd7.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p><em>Ảnh camera chế độ ch&acirc;n dung</em></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; l&agrave; t&iacute;nh năng&nbsp;Deep Fusion&nbsp;được quảng c&aacute;o l&agrave; cơ chế chụp h&igrave;nh mới, đem lại h&igrave;nh ảnh với độ chi tiết cao, dải tần nhạy s&aacute;ng rộng v&agrave; rất &iacute;t bị nhiễu.</p>\r\n\r\n<p><em>Một bức ảnh chụp đủ s&aacute;ng tr&ecirc;n iPhone 11</em></p>\r\n\r\n<p>Cụ thể, khi người d&ugrave;ng bấm n&uacute;t chụp, thiết bị sẽ thực hiện tổng cộng 9 bức h&igrave;nh c&ugrave;ng l&uacute;c, gồm một tấm ch&iacute;nh v&agrave; t&aacute;m tấm phụ, sau đ&oacute; chọn ra c&aacute;c điểm ảnh tốt nhất để đưa v&agrave;o tấm ảnh cuối c&ugrave;ng nhằm cải thiện chi tiết v&agrave; khử nhiễu.</p>\r\n\r\n<p><em>Quả b&oacute;ng qua ống k&iacute;nh chế độ b&igrave;nh thường của iPhone 11</em></p>\r\n\r\n<p>V&agrave; điều được người d&ugrave;ng mong chờ nhất ch&iacute;nh l&agrave; t&iacute;nh năng chụp đ&ecirc;m cũng xuất hiện tr&ecirc;n chiếc iPhone mới n&agrave;y với t&ecirc;n gọi Night Mode.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd15-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Ảnh chụp chế độ Night Mode\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd15-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p><em>Ảnh chụp với chế độ Night Mode</em></p>\r\n\r\n<p>Night Mode sẽ tự động k&iacute;ch hoạt khi bạn chụp ảnh trong điều kiện thiếu s&aacute;ng v&agrave; chất lượng ảnh cho ra l&agrave; rất ấn tượng khi so s&aacute;nh với những chiếc iPhone đời cũ.</p>\r\n\r\n<p>Camera trước cũng đ&atilde; tốt hơn nhiều</p>\r\n\r\n<p>Đầu ti&ecirc;n phải kể đến l&agrave; độ ph&acirc;n giải camera trước đ&atilde; được n&acirc;ng từ 7 MP l&ecirc;n th&agrave;nh 12 MP cho người d&ugrave;ng những bức ảnh chi tiết hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd12-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Cụm camera phía trước ở tai thỏ\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd12-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>iPhone 11 cũng l&agrave; chiếc&nbsp;iPhone&nbsp;đầu ti&ecirc;n hỗ trợ quay video si&ecirc;u chậm ở camera trước với t&ecirc;n gọi l&agrave;&nbsp;Slofies, bạn c&oacute; thể c&oacute; được cho m&igrave;nh những thước phim kh&aacute; th&uacute; vị với t&iacute;nh năng n&agrave;y.</p>\r\n\r\n<p>Apple cũng rất &quot;t&acirc;m l&yacute;&quot; khi trang bị th&ecirc;m t&iacute;nh năng tự động thay đổi g&oacute;c chụp rộng hơn khi bạn xoay ngang chiếc iPhone của m&igrave;nh để c&oacute; thể thoải m&aacute;i selfie với bạn b&egrave;.</p>\r\n\r\n<p><em>Ảnh chụp selfie tr&ecirc;n iPhone 11</em></p>\r\n\r\n<p>V&agrave; Face ID tr&ecirc;n iPhone 11 cũng được cải tiến để c&oacute; thể nhận diện ở g&oacute;c rộng hơn v&agrave; c&oacute; tốc độ nhanh hơn so với iPhone Xr cũ.</p>\r\n\r\n<p>Hiệu năng mạnh mẽ h&agrave;ng đầu thế giới</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd4-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Điểm hiệu năng Antutu Benchmark\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd4-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Hiệu năng lu&ocirc;n l&agrave; vấn đề m&agrave; người d&ugrave;ng iPhone chưa bao giờ phải ph&agrave;n n&agrave;n v&agrave; với&nbsp;iPhone 11 128 GB năm nay cũng kh&ocirc;ng phải l&agrave; ngoại lệ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd4.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Trải nghiệm chơi game trên iPhone 11\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd4.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>M&aacute;y sở hữu con chip mạnh mẽ nhất của Apple cho những chiếc iPhone năm 2019&nbsp;Apple A13 Bionic&nbsp;c&ugrave;ng 4 GB RAM v&agrave; 128 GB bộ nhớ trong gi&uacute;p&nbsp;điện thoại chơi game&nbsp;tốt mượt m&agrave; với mức cấu h&igrave;nh max setting cũng như mọi thao t&aacute;c cơ bản đều cho tốc độ phản hồi nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd6-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Hiệu năng mạng mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd6-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Con chip mới n&agrave;y cũng l&agrave; CPU&nbsp;di động tiết kiệm năng lượng nhất từ ​​trước đến nay nhờ x&acirc;y dựng tr&ecirc;n kiến tr&uacute;c 7 nm v&agrave; cung cấp đến 8.5 ngh&igrave;n tỷ b&oacute;ng b&aacute;n dẫn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd5-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Cụm camera phía trước ở tai thỏ\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd5-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Với những trang bị như vậy th&igrave; bạn c&oacute; thể chơi mọi tựa game, sử dụng mọi ứng dụng nặng nhất hiện nay m&agrave; vẫn đảm bảo m&aacute;y hoạt động trơn tru v&agrave; mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd44.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Trải nghiệm chơi game trên iPhone 11\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd44.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>C&ugrave;ng với iPhone 11 th&igrave; Apple cũng ch&iacute;nh thức mang iOS 13 tới với người d&ugrave;ng với nhiều cải tiến đ&aacute;ng gi&aacute; như&nbsp;chế độ Dark Mode&nbsp;hay khả năng tối ưu h&oacute;a khi sạc pin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd10-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Thời lượng pin dài\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd10-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra chiếc iPhone n&agrave;y c&ograve;n hỗ trợ&nbsp;Wi-Fi 6 với tốc độ nhanh hơn v&agrave; kết nối ổn định hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd11-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Khay sim\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd11-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Pin đ&atilde; tốt nay c&ograve;n tốt hơn</p>\r\n\r\n<p>Như c&aacute;c bạn đ&atilde; biết th&igrave; iPhone Xr năm ngo&aacute;i l&agrave; chiếc iPhone c&oacute; thời lượng pin tốt nhất từ trước tới nay của Apple.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd45.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Giao diện hiển thị pin trên máy\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd45.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>V&agrave; iPhone 11 năm nay thậm ch&iacute; Apple c&ograve;n l&agrave;m tốt hơn thế với th&ecirc;m 1 giờ sử dụng pin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd39.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Thời lượng sử dụng pin\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd39.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Apple cũng n&oacute;i rằng họ đ&atilde; cải tiến b&ecirc;n trong để chiếc iPhone 11 mới c&oacute; thể&nbsp;<a href=\"https://www.thegioididong.com/dtdd-chong-nuoc-bui\" target=\"_blank\">chống nước</a>&nbsp;tốt hơn thế hệ cũ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd9-1.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Thiết kế thời trang\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd9-1.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>V&agrave; cũng kh&ocirc;ng thể kh&ocirc;ng nhắc tới thiết kế&nbsp;k&iacute;nh mới ở cả mặt trước v&agrave; mặt sau của iPhone 11 v&agrave; được Apple giới thiệu l&agrave; loại k&iacute;nh bền nhất từng được sử dụng tr&ecirc;n smartphone.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/210644/iphone-11-128gb-tgdd41.jpg\"><img alt=\"Điện thoại iPhone 11 128GB | Nhiều màu sắc đa dạng\" src=\"https://cdn.tgdd.vn/Products/Images/42/210644/iphone-11-128gb-tgdd41.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>M&agrave;u sắc mới tr&ecirc;n chiếc iPhone n&agrave;y hứa hẹn cũng sẽ khiến người d&ugrave;ng phải m&ecirc; mệt v&agrave; muốn bỏ tiền ra sở hữu ngay v&agrave; lu&ocirc;n một chiếc.</p>\r\n', '2021-01-02', 1, '19.490'),
(224, 'Điện thoại iPhone 12 128GB', '26.990', 'iphone-12-xanh-duong-new-600x600-600x600.jpg', 18, '<h3>Mới đ&acirc;y,&nbsp;Apple&nbsp;đ&atilde; tr&igrave;nh diện đến người d&ugrave;ng mẫu điện thoại&nbsp;iPhone 12 128 GB&nbsp;với sự tuy&ecirc;n bố về một kỷ nguy&ecirc;n mới của&nbsp;iPhone: iPhone 5G si&ecirc;u nhanh, n&acirc;ng cấp về m&agrave;n h&igrave;nh v&agrave; hiệu năng hứa hẹn đ&acirc;y sẽ l&agrave; smartphone cao cấp đ&aacute;ng ch&uacute; &yacute; nhất trong năm nay.&nbsp;</h3>\r\n\r\n<h3>Hiệu năng vượt trội, th&aacute;ch thức mọi giới hạn</h3>\r\n\r\n<p>iPhone 12 được trang bị&nbsp;chipset A14 Bionic&nbsp;- bộ xử l&yacute; được trang bị lần đầu tr&ecirc;n iPad Air 4 vừa được ra mắt c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, mở đầu xu thế chip 5 nm thương mại tr&ecirc;n to&agrave;n thế giới.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-144220-034232.jpg\"><img alt=\"Trang bị vi xử lý A14 Bionic | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-144220-034232.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Apple cho biết A14 Bionic sẽ mạnh hơn gần 40%&nbsp;chipset A13 Bionic, trong đ&oacute; khả năng xử l&yacute; đồ họa nhanh hơn 50% cũng như c&aacute;c t&aacute;c vụ học m&aacute;y mượt m&agrave; hơn 80% khi so s&aacute;nh với bộ xử l&yacute; tiền nhiệm.</p>\r\n\r\n<p>Apple cũng đ&atilde; hỗ trợ kết nối mạng 5G tốc độ load cực nhanh cho mọi trải nghiệm lướt web, xem phim lu&ocirc;n được ổn định v&agrave; mượt m&agrave; hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-143520-043510.jpg\"><img alt=\"Hỗ trợ 5G cho tốc độ load cực nhanh | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-143520-043510.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>iPhone 12 cũng được t&iacute;ch hợp th&ecirc;m chế độ &ldquo;Smart Data Mode&rdquo; cho ph&eacute;p m&aacute;y tự động ph&aacute;t hiện ứng dụng, c&aacute;c thao t&aacute;c n&agrave;o cần nhiều băng th&ocirc;ng để k&iacute;ch hoạt 5G. Nếu sử dụng c&aacute;c t&aacute;c vụ th&ocirc;ng thường m&aacute;y sẽ chuyển sang chế độ 4G LTE ph&ugrave; hợp với từng điều kiện sử dụng, gi&uacute;p tiết kiệm dữ liệu, hạn chế n&oacute;ng m&aacute;y v&agrave; duy tr&igrave; thời lượng pin sử dụng cho người d&ugrave;ng.</p>\r\n\r\n<p>Được trang bị con chip mạnh nhất hiện nay đi k&egrave;m với khả năng kết nối mạng 5G gi&uacute;p&nbsp;điện thoại chơi game&nbsp;mượt m&agrave; nay lại mượt m&agrave; hơn. Với sự chuyển động trơn tru, tốc độ load nhanh với độ trễ gần như bằng kh&ocirc;ng chắc chắn sẽ đem đến sự h&agrave;i l&ograve;ng đến với người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-144020-034022.jpg\"><img alt=\"Hiệu năng mạnh chiến game mượt mà | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-144020-034022.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a><br />\r\nNgo&agrave;i ra, đi k&egrave;m với m&aacute;y l&agrave;&nbsp;bộ nhớ trong 128 GB&nbsp;cho khả năng lưu trữ mọi dữ liệu như h&igrave;nh ảnh, video hay c&aacute;c ứng dụng. Nếu nhu cầu lưu trữ qu&aacute; cao bạn c&oacute; thể tham khảo phi&ecirc;n bản&nbsp;iPhone 12 256 GB&nbsp;hoặc c&oacute; thể lưu tr&ecirc;n c&aacute;c tiện &iacute;ch đ&aacute;m m&acirc;y.</p>\r\n\r\n<h3>Vẻ đẹp gợi nhớ v&agrave; chuẩn m&agrave;n h&igrave;nh OLED</h3>\r\n\r\n<p>iPhone 12 lấy cảm hứng vẻ đẹp ho&agrave;i niệm của iPhone 4 v&agrave; iPhone 5 từ những năm về trước hay c&aacute;c d&ograve;ng&nbsp;iPad Pro&nbsp;v&agrave;&nbsp;iPad Air&nbsp;mới nhất thay v&igrave; bo cong như&nbsp;iPhone 11.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-142120-042144.jpg\"><img alt=\"Thiết kế trong vuông vắn hơn | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-142120-042144.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh iPhone 12 c&oacute; k&iacute;ch thước 6.1 inch độ ph&acirc;n giải 1170 x 2532 Pixels, khung viền bao quanh mặt k&iacute;nh được l&agrave;m từ nh&ocirc;m thay v&igrave; th&eacute;p tr&ecirc;n d&ograve;ng Pro.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh Super Retina XDR&nbsp;OLED tr&agrave;n viền được trang bị tr&ecirc;n iPhone 12 cho trải nghiệm xem được s&aacute;ng hơn, đắm ch&igrave;m hơn. Hỗ trợ True Tone v&agrave; c&oacute; độ s&aacute;ng tối đa l&agrave; 1.200 nits ở HDR v&agrave; độ s&aacute;ng ti&ecirc;u biểu tối đa l&agrave; 625 nits từ đ&oacute; gi&uacute;p m&aacute;y đủ s&aacute;ng để hoạt động tốt khi sử dụng dưới &aacute;nh nắng mặt trời.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-123020-113018.jpg\"><img alt=\"Màn hình Super Retina XDR OLED | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-123020-113018.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Tần số qu&eacute;t chỉ l&agrave; 60 Hz nhưng b&ugrave; v&agrave;o đ&oacute; m&aacute;y được bảo vệ bởi một c&ocirc;ng nghệ mới gọi l&agrave; Ceramic Shield, mang đến bước tiến nhảy vọt về độ bền chưa từng c&oacute; tr&ecirc;n iPhone. Apple cho biết vật liệu mới n&agrave;y c&oacute; khả năng chống vỡ gấp 4 lần so với&nbsp;k&iacute;nh cường lực Gorilla Glass&nbsp;cho bạn y&ecirc;n t&acirc;m hơn trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n\r\n<p>Để ph&ugrave; hợp với t&iacute;nh c&aacute;ch cũng như sở th&iacute;ch ri&ecirc;ng biệt của mỗi người iPhone 12 ra mắt với 5 m&agrave;u sắc kh&aacute;c nhau cho bạn c&oacute; th&ecirc;m nhiều sự lựa chọn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-144320-034353.jpg\"><img alt=\"Nhiều màu sắc tăng thêm sự lựa chọn | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-144320-034353.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<h3>Hệ thống camera k&eacute;p chuy&ecirc;n nghiệp</h3>\r\n\r\n<p>iPhone 12 c&oacute; 2 cảm biến để cải thiện khả năng chụp ảnh, đ&acirc;y l&agrave; c&aacute;c ống k&iacute;nh ti&ecirc;u chuẩn 12 MP khẩu độ f/1.6 v&agrave;&nbsp;camera g&oacute;c&nbsp;si&ecirc;u rộng&nbsp;12 MP khẩu độ f/2.4 cho g&oacute;c nh&igrave;n 120 độ đem đến những khung ảnh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ một c&aacute;ch bao qu&aacute;t hay những t&ograve;a nh&agrave; cao tầng chỉ trong một khung h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-144420-034454.jpg\"><img alt=\"Hệ thống camera sau | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-144420-034454.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Theo Apple, ống k&iacute;nh khẩu độ lớn tr&ecirc;n m&aacute;y sẽ gi&uacute;p khả năng chụp ảnh thiếu s&aacute;ng được cải thiện th&ecirc;m 27% v&agrave; giảm nhiễu hơn so với thế hệ trước. V&igrave; vậy, cho d&ugrave; ban ng&agrave;y hay ban đ&ecirc;m với &aacute;nh s&aacute;ng yếu, bạn sẽ nhận được mức độ chi tiết v&agrave; m&agrave;u sắc m&agrave; trước đ&acirc;y kh&ocirc;ng thể c&oacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-141020-041016.jpg\"><img alt=\"Chế độ ban đêm  | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-141020-041016.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Thiết bị c&ograve;n hỗ trợ c&aacute;c t&iacute;nh năng ti&ecirc;u chuẩn như chế độ ch&acirc;n dung, zoom kỹ thuật số 5x, to&agrave;n cảnh. Ngo&agrave;i ra, t&iacute;nh năng n&acirc;ng cao như&nbsp;Smart HDR&nbsp;3 mới để gi&uacute;p bạn chụp ảnh với m&agrave;u sắc v&agrave; độ tương phản được cải thiện, cũng như c&aacute;c cải tiến về chế độ ban đ&ecirc;m.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-141420-041412.jpg\"><img alt=\"HDR 3 cân bằng các yếu tố trong ảnh | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-141420-041412.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Chế độ ch&acirc;n dung thậm ch&iacute; c&ograve;n tốt hơn trong việc l&agrave;m mờ hậu cảnh một c&aacute;ch nghệ thuật để dồn to&agrave;n bộ sự tập trung v&agrave;o đối tượng của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-141220-041255.jpg\"><img alt=\"Ảnh chụp chân dung | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-141220-041255.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>T&iacute;nh năng&nbsp;Deep Fusion&nbsp;trong điều kiện &aacute;nh s&aacute;ng từ trung b&igrave;nh đến yếu n&oacute; ph&acirc;n t&iacute;ch nhiều độ phơi s&aacute;ng để tối đa h&oacute;a chi tiết gi&uacute;p bạn c&oacute; c&oacute; một g&oacute;c nh&igrave;n tuyệt đẹp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-141720-041720.jpg\"><img alt=\"Phân tích nhiều độ phơi sáng để tối đa hóa chi tiết | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-141720-041720.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Ở phần notch mặt trước của điện thoại iPhone 12 c&ograve;n c&oacute; camera selfie 12 MP ở khẩu độ f/2.2 v&agrave; mang nhiều t&iacute;nh năng tương tự như m&aacute;y ảnh ch&iacute;nh, chẳng hạn như: Chế độ ban đ&ecirc;m, Deep Fusion v&agrave; Smart HDR 3 c&ugrave;ng nhiều c&ocirc;ng nghệ kh&aacute;c mang đến chất lượng h&igrave;nh ảnh r&otilde; n&eacute;t v&agrave; ho&agrave;n mỹ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-140420-040401.jpg\"><img alt=\"Phần notch chứa camera selfie | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-140420-040401.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<h3>C&ocirc;ng nghệ sạc đa năng</h3>\r\n\r\n<p>iPhone 12 c&oacute; khả năng l&ecirc;n đến 17 giờ ph&aacute;t video v&agrave; nghe nhạc li&ecirc;n tục trong khoảng 65 giờ cho mọi trải nghiệm của bạn lu&ocirc;n diễn ra một c&aacute;ch liền mạch.</p>\r\n\r\n<p>Một iPhone cao cấp phục vụ mọi c&ocirc;ng việc kh&ocirc;ng thể thiếu t&iacute;nh năng&nbsp;sạc pin nhanh. iPhone 12 cũng được trang bị sạc nhanh 20 W sẽ đảm bảo thiết bị c&oacute; ngay 50% pin trong 30 ph&uacute;t. Ngo&agrave;i ra, cũng c&oacute; t&iacute;nh năng&nbsp;sạc kh&ocirc;ng d&acirc;y&nbsp;Qi, cho ph&eacute;p nạp năng lượng tr&ecirc;n đế sạc kh&ocirc;ng d&acirc;y hữu &iacute;ch v&agrave; tiện lợi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-144720-034704.jpg\"><img alt=\"Tích hợp nhiều công nghệ sạc | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-144720-034704.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Thiết bị hỗ trợ t&iacute;nh năng sạc kh&ocirc;ng d&acirc;y&nbsp;MagSafe&nbsp;mới của Apple với c&ocirc;ng suất 15 W gi&uacute;p cho qu&aacute; tr&igrave;nh sạc trở n&ecirc;n dễ d&agrave;ng hơn bao giờ hết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/228736/iphone-12-128gb-145420-035457.jpg\"><img alt=\"Bao da ôm trọn thân máy thành đồng hồ điện tử | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/228736/iphone-12-128gb-145420-035457.jpg\" style=\"border-style:initial; border-width:0px; height:auto; margin:10px auto\" /></a></p>\r\n\r\n<p>Sự trở lại của iPhone 12 kh&ocirc;ng chỉ đơn thuần l&agrave; ra mắt, sự lột x&aacute;c sản phẩm lần n&agrave;y của Apple m&agrave; c&ograve;n mở ra một kỷ nguy&ecirc;n mới về tốc độ mạng 5G. Hứa hẹn đ&acirc;y l&agrave; smartphone sẽ l&agrave;m mưa l&agrave;m gi&oacute; trong những th&aacute;ng cuối năm 2020 với nhiều điểm vượt trội.</p>\r\n', '2021-01-02', 1, '24.990');

